import React from "react";
import '../css/Contact.css';
class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      images: [
        "assets/images/hostel7.jpg",
        "https://picsum.photos/200/300/?image=524"
      ],
      selectedImage: "https://picsum.photos/200/300/?image=523"
    };
  }

  componentDidMount() {
    setInterval(() => {
      this.setState(prevState => {
        if (prevState.selectedImage === this.state.images[0]) {
          return {
            selectedImage: this.state.images[1]
          };
        } else {
          return {
            selectedImage: this.state.images[0]
          };
        }
      });
    }, 3000);
  }

  render() {
    return (
      <div className="myslide fade">
        <img src={this.state.selectedImage} alt="" width="100%" height="500px" style={{marginLeft:"auto"}}/>
        <div className="text">caption</div>
      </div>
    );
  }
}

export default Contact;
