import React from 'react'
import PayWithPayPal from './PayWithPayPal';
import firebase from '../Config/Config';
class Paypal extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            product:[],
            key:'',
            checkoutList: [],
            isCheckout: false
        }
    }
    componentDidMount(){
        const ref=firebase.firestore().collection("Products").doc(this.props.match.params.id);
        ref.get().then((doc)=>{
            if(doc.exists){
                this.setState({
                    product:doc.data(),
                    key:doc.id,
                    isloading:false
                });
            }else{
                console.log("No such documnet is here")
            }
        })
    }

    render () {
        const { total, checkoutList, isCheckout } = this.state

        if (isCheckout) {
            return (
                <PayWithPayPal
                    total={this.state.product.price}
                    items={checkoutList}
                />
            )
        }
        return (
            <React.Fragment>
                <div className="home-container">
                    <div className="brand">hostel:  {this.state.product.name}</div>
                    <div className="checkout">
                        <div className="checkout-total">Total: Rs. {this.state.product.price}/-</div>
                        <button
                            className="checkout-button"
                            onClick={() => {
                                if (this.state.product.price > 0) {
                                    this.setState({ isCheckout: true })
                                }
                            }}
                        >
                            Checkout 
                        </button>
                    </div>
                </div>
                
            </React.Fragment>
        )
    }
}

export default Paypal;
