import React, { Component } from 'react';
import firebase from '../Config/Config';
class AddProduct extends Component {
    constructor(props) {
        super(props);
        this.ref=firebase.firestore().collection('Products');
        this.state = { 
            address:'',
            city:'',
            name:'',
            contact:'',
            email:'',
            price:'',
            url:'',
            image:null
        }
    }

    onInputChange=(e)=>{
        const state=this.state;
        state[e.target.name]=e.target.value;
        this.setState(state);
    }
    handleChange=(e)=>{
        if(e.target.files[0]){
            this.setState({
                image:e.target.files[0]
            })
        }
        console.log(e.target.files[0])
    }
    handleUplaod=()=>{
        const {image}=this.state;
        const uploadTask=firebase.storage().ref(`images/${image.name}`).put(this.state.image)
        uploadTask.on('state_changed',(snapshot)=>{console.log('snapshot')},
        (error)=>{console.log(error);},
        ()=>{firebase.storage().ref('images').child(image.name).getDownloadURL().then(url=>this.setState({url}))})
    }
    onSubmit=(e)=>{
        e.preventDefault();
        const { address,city,name,contact,email,price}=this.state;
        this.ref.add({
            address,city,name,email,contact,price,
            url:this.state.url
        }).then((docRef)=>{
            this.setState({
                address:'',
                city:'',
                name:'',
                contact:'',
                email:'',
                price:'',
                url:''

            });
            this.props.history.push("/Landing")
        })
        .catch((error)=>{
            console.error("Error Adding document :",error);
        })
    }
    render() { 
        const{address,city,name,contact,email,price}=this.state;
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo01" aria-controls="navbarTogglerDemo01" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div className="collapse navbar-collapse" id="navbarTogglerDemo01">
    <a className="navbar-brand" href="/">Logo</a>
    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
      <li className="nav-item active">
        <a className="nav-link" href="/Landing">Home <span class="sr-only">(current)</span></a>
      </li>
      <li className="nav-item">
        <a className="nav-link" href="/Landing">Show Room</a>
      </li>
      <li className="nav-item">
        <a className="nav-link disabled" href="/Logout">Log Out</a>
      </li>
    </ul>
    
  </div>
</nav>
                
            <div className="container">
                <div className="py-4">
                <div className="row">
                    <div className="col-md-10 mx-auto">
                        <div className="card card-body shadow">
             
                            <div className="form-row form-group mb-4">
                            <div className="col-md-6">
                                <input
                                for="name"
                                type="text"
                                placeholder="Enter Hostel Name"
                                name="name"
                                value={name}
                                onChange={this.onInputChange}
                                className="form-control"
                              
                                />
                            </div>
                            <div className="col-md-6">
                                <input
                                placeholder="Enter Hostel E-mail"
                                name="email"
                                value={email}
                                onChange={this.onInputChange}
                                className="form-control"
                                type="email" for="email"
                                />
                            </div>
                            </div>
                            <div className="form-row form-group mb-4">
                            <div className="col-md-6">
                                <input
                                type="text"
                                placeholder="Enter Hostel Phone"
                                name="contact" for="contact"
                                value={contact}
                                onChange={this.onInputChange}
                                className="form-control"
                             
                                />
                            </div>
                            <div className="col-md-6">
                                <input
                                type="text"
                                placeholder="Enter Hostel price"
                                name="price"
                                value={price}
                                onChange={this.onInputChange}
                                className="form-control"
                                
                                />
                            </div>
                            </div>
                            <div className="form-row form-group">
                            <div className="col-md-6">
                                <input
                                type="text"
                                placeholder="Enter Hostel Address "
                                value={address}
                                name="address"
                                onChange={this.onInputChange}
                                className="form-control"
                                
                                />
                            </div>
                            <div className="col-md-6">
                                <input
                                type="text"
                                placeholder="Enter Hostel city"
                                value={city}
                                name="city" for="city"
                                onChange={this.onInputChange}
                                className="form-control"
                               
                                />
                            </div>
                            </div>
                            <div style={{ margin: '100px' }}>
				
			</div>
                            <div className="upload-data">
                        <input type="file" onChange={this.handleChange}/>
                        <img src={this.state.url} height="200" width="200" alt=""/>
                    </div>
                    <div className="button">
                            <button className="submit-button btn-outline-secondary" onClick={this.handleUplaod}>Upload Image First</button>
                            <button className="submit-button btn-outline-secondary" onClick={this.onSubmit}>save</button>
                    </div>
                                
        
                        </div>
                    </div>
                    </div>
                </div>
                </div>
            </div>

          );
    }
}
 
export default AddProduct;
