import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import firebase from '../Config/Config';
class Landing extends Component {
    constructor(props) {
        super(props);
        this.ref=firebase.firestore().collection("Products");
        this.unsubscribe=null;
        this.state={
            products : []
        };
    }
    componentDidMount(){
        this.unsubscribe=this.ref.onSnapshot(this.onCollectionUpdate);
    }
    onCollectionUpdate=(querySnapshot)=>{
        const products=[];
        querySnapshot.forEach((doc)=>{
            const { address,city,name,contact,email,price,url}=doc.data();
            products.push({
                key:doc.id,
                doc,
                address,
                city,
                name,
                contact,
                email,
                price,
                url
            });
        });
        this.setState({products});
    }
    render() { 
        return ( 
            <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo01" aria-controls="navbarTogglerDemo01" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div className="collapse navbar-collapse" id="navbarTogglerDemo01">
    <a className="navbar-brand" href="">Hidden brand</a>
    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
      <li className="nav-item active">
        <a className="nav-link" href="/Landing">Home <span class="sr-only">(current)</span></a>
      </li>
      <li className="nav-item">
        <a className="nav-link" href="/AddProduct">Add Room</a>
      </li>
      <li className="nav-item">
        <a className="nav-link disabled" href="/Logout">Log Out</a>
      </li>
    </ul>
    
  </div>
</nav>
                <Link to="/create">
                <button>Add Hostel/PG</button>
                </Link>
                <div className="container">
  <div className="py-4">
    <div className="row">
     {this.state.products.map(product=>(
      <div className="col-lg-3 col-md-6 mb-4">
        <div className="card shadow text-center py-4">
          <img src={product.url} height="100px" alt=""/>
          <div className="card-body">
     <h5 className="card-title mb-0">{product.name}</h5>
     <p className="text-muted small">{product.city}</p>
            <Link to={`/show/${product.key}`} className="btn btn-primary btn-profile">
              View Room
            </Link>
            
          </div>
        </div>
      </div>
        ))}
        </div>
        </div>
        </div>
            </div>
         );
    }
}
 
export default Landing;
