import React from 'react';
import '../App.css';
import firebase from '../Config/Config';
import {Link} from 'react-router-dom';
class ShowProduct extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            product:[],
            key:''
        }
    }
    componentDidMount(){
        const ref=firebase.firestore().collection("Products").doc(this.props.match.params.id);
        ref.get().then((doc)=>{
            if(doc.exists){
                this.setState({
                    product:doc.data(),
                    key:doc.id,
                    isloading:false
                });
            }else{
                console.log("No such documnet is here")
            }
        })
    }
    delete(id){
        var desertRef=firebase.storage().refFromURL(this.state.product.url)
        firebase.firestore().collection('Products').doc(id).delete().then(()=>{
            console.log("Document is successfully deleted");
            this.props.history.push("/")
        }).catch((error)=>{
            console.log("error:",error)
        });
        desertRef.delete().then(function(){
            console.log("file deleted")
        }).catch(function(error){
            console.log("error while deleting the file")
        })
    }
    
    render() { 
        return ( 
            <div>
                <Link to="/">
              <button>show</button>
                </Link>
                <div className="upload-data">
                    <img src={this.state.product.url} height="200" width="200" alt="" />

                </div>
                <div>
                   <div className="container">
                       <div className="panel panel-default">
                            <h3 className="panel-title">{this.state.product.name}</h3>
                       </div>
                       <div className="panel-body">
                           <dl>
                                <dt>Addresss:</dt>
                                 <dd>{this.state.product.address}</dd>
                            </dl>
                            <Link to={`/edit/${this.state.key}`} className="btn btn-success">Edit</Link>
                            <button onClick={this.delete.bind(this,this.state.key)} className="btn btn-danger">Delete</button>
                       </div>
                   </div>
        </div>
        <div class="container py-3">
 
  <div class="card">
    <div class="row ">

      <div class="col-md-7 px-3">
        <div class="card-block px-6">
          <h4 class="card-title">{this.state.product.name}</h4>
          <p class="card-text">
          <dt>Addresss:{this.state.product.address}</dt>
          </p>
          <p class="card-text">Made for usage, commonly searched for. Fork, like and use it. Just move the carousel div above the col containing the text for left alignment of images</p>
          <br/>
          <div className="panel-body">
                           <dl>
                                <dt>Addresss:{this.state.product.address}</dt>
                                 
                            </dl>
                            <Link to={`/edit/${this.state.key}`} className="btn btn-success">Edit</Link>
                            <button onClick={this.delete.bind(this,this.state.key)} className="btn btn-danger">Delete</button>
                       </div>
        </div>
      </div>
      <div class="col-sm-5">
        <img class="d-block w-100" src={this.state.product.url} alt=""></img>
        </div>
            </div>
            </div>
            </div>
            </div>
         );
    }
}
 
export default ShowProduct;
