import React,{Component} from 'react';
import '../App.css';
import firebase from '../Config/Config';
import {Link} from 'react-router-dom';
class EditProduct extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            key:'',
            address:'',
            city:'',
            name:'',
            contact:'',
            email:'',
            price:'',
            url:'',
            image:null,
            facilities:[]
         }
    }
    componentDidMount(){
        const ref=firebase.firestore().collection("Products").doc(this.props.match.params.id);
        ref.get().then((doc)=>{
            if(doc.exists){
                const documnet=doc.data();
                this.setState({
                   key:doc.id,
                   address:documnet.address,
                   city:documnet.city,
                   name:documnet.name,
                   contact:documnet.contact,
                   email:documnet.email,
                   price:documnet.price,
                   url:documnet.url,
                   facilities:documnet.facilities
                });
            }else{
                console.log("No such documnet is here")
            }
        })
    }
    handleChange=(e)=>{
        if(e.target.files[0]){
            this.setState({
                image:e.target.files[0]
            })
        }
        console.log(e.target.files[0])
    }
    handleUplaod=()=>{
        const {image,url}=this.state;
        var desertRef=firebase.storage().refFromURL(url)
        const uploadTask=firebase.storage().ref(`images/${image.name}`).put(this.state.image)
        uploadTask.on('state_changed',(snapshot)=>{console.log('snapshot')},
        (error)=>{console.log(error);},
        ()=>{firebase.storage().ref('images').child(image.name).getDownloadURL().then(url=>this.setState({url}))})
        desertRef.delete().then(function(){
            console.log("file deleted")
        }).catch(function(error){
            console.log("error while deleting the file")
        })
    }
    onChange=(e)=>{
        const state=this.state;
        state[e.target.name]=e.target.value;
        this.setState({document:state});
    }
    onSubmit=(e)=>{
        e.preventDefault();
        const { name,description,url}=this.state;
        const updateRef=firebase.firestore().collection('Products').doc(this.state.key);
        updateRef.set({
            name,description,
            url
        }).then((docRef)=>{
            this.setState({
                key:'',
                name:'',
                description:'',
                url:''

            });
            this.props.history.push("/show/"+this.props.match.params.id)
        })
        .catch((error)=>{
            console.error("Error editing the document :",error);
        })
    }
    render() { 
        return ( 
        <div>
            <Link to="/">
              <button>show</button>
                </Link>
                
                <div className="upload-data">
                        <input type="file" onChange={this.handleChange}/>
                        <img src={this.state.url} height="200" width="200" alt=""/>
                    </div>
                    <div className="button">
                            <button className="submit-button" onClick={this.handleUplaod}>Upload Image</button>
                           
                    </div>
                    <div className="container">
                       <div className="panel panel-default">
                       
                   
                       <div className="panel-body">
                           <form onSubmit={this.onSubmit}>
                               <div>
                           <div className="form-group"></div>
                        <label for="name">Product name </label>
                        <input type="text" className="form-control" name="name" value={this.state.name} onChange={this.onChange} placeholder="please enter name"></input>
                    </div>
                    <div>
                    <div className="form-group"></div>
                        <label for="description">Product dec </label>
                        <textarea type="text" className="form-control" name="description"  onChange={this.onChange} placeholder="please enter address" value={this.state.description}></textarea>
                    </div>
                    <button type="submit" className="btn btn-success">submit</button>
                           </form>
                       </div>
                   </div>
                   </div>
        </div> 
        );
    }
}
 
export default EditProduct;
