import React, { Component } from 'react';
import axios from 'axios';
class ConfirmRoom extends Component {
    state={
        name:'',
        email:'',
        message:'',
        sent:false
    }
    handleName=(e)=>{
        this.setState({
            name:e.target.value
        })
    }
    handleEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    handleMessage=(e)=>{
        this.setState({
            message:e.target.value
        })
    }
    formSubmit=(e)=>{
        e.preventDefault();
        let data={
            name:this.state.name,
            email:this.state.email,
            message:this.state.message
        }
        axios.post('/api/forma',data)
        .then(res=>{
            this.setState({
                sent:true,
            },this.resetForm())
        })
        .catch(()=>{
            console.log('message not send ')
        })

    }
    resetForm=()=>{
        this.setState({
            name:'',
            email:'',
            message:''
        })
        setTimeout(() => {
            this.setState({
                sent:false,
            })
        }, 3000);
    }
    render() { 
        return ( 
            <div>
                <div className="container register">
                <div className="row">
                    <div className="col-md-3 register-left">
                        <img src="https://image.ibb.co/n7oTvU/logo_white.png" alt=""/>
                        <h3>Welcome</h3>
                        <p>Just send you confirmation of booking</p>
                    </div>
                    <div className="col-md-9 register-right">
                        <div className="tab-content" id="myTabContent">
                            <div className="tab-pane fade show active" id="home" role="tabpanel" aria-labelledby="home-tab">
                                <h3 className="register-heading">Welcome Student</h3>
                                <div className=" register-form">
                                   
                                    <form onSubmit={this.formSubmit}>
                                            <div className="form-group">
                                                <label htmlFor="name">NAME</label>
                                                <input type="text" 
                                                name="name" 
                                                placeholder="NAME....." 
                                                onChange={this.handleName} 
                                                value={this.state.name}
                                                className="form-control"
                                                ></input>
                                            </div>
                                            <div class-Name="form-group">
                                                <label htmlFor="email">EMAIL</label>
                                                <input type="text" name="email" placeholder="EMAIL....." className="form-control"  onChange={this.handleEmail} value={this.state.email}
                                                ></input>
                                            </div>
                                            <div className="form-group textArea">
                                                <label htmlFor="message">MESSAGE</label>
                                                <textarea type="text" name="message" placeholder="MESSAGE....." className="form-control" onChange={this.handleMessage}
                                                rows="2" cols="10"
                                                value={this.state.message}
                                                ></textarea>
                                            </div>
                                            <div className={this.state.sent?'msg msgAppear':'msg'}>
                                                message has been sent
                                            </div>
                                            <button className="btn" type="submit">submit</button>
                                        </form>
                                    </div>
                                    
                            
                            </div>
                        </div>
                    </div>
                </div>

            </div>
            </div>
         );
    }
}
 
export default ConfirmRoom;
