import React from 'react'
import PayWithPayPal from './PayWithPayPal';
import firebase from '../Config/Config';
class Paypal extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            product:[],
            key:'',
            total: 0.00,
            checkoutList: [],
            isCheckout: false
        }
    }
    componentDidMount(){
        const ref=firebase.firestore().collection("Products").doc(this.props.match.params.id);
        ref.get().then((doc)=>{
            if(doc.exists){
                this.setState({
                    product:doc.data(),
                    key:doc.id,
                    isloading:false
                });
            }else{
                console.log("No such documnet is here")
            }
        })
    }
    onAdd = (name, value) => {
        this.setState({
            checkoutList: [...this.state.checkoutList, { name, value }],
            total: this.state.total + value
        })
    }

    render () {
        const { total, checkoutList, isCheckout } = this.state

        if (isCheckout) {
            return (
                <PayWithPayPal
                    total={this.state.product.price}
                    items={checkoutList}
                />
            )
        }
        return (
            <React.Fragment>
                <div className="home-container">
                    <div className="brand">hostel</div>
                    <div className="checkout">
                        <div className="checkout-total">Total: Rs. {this.state.product.price}/-</div>
                        <button
                            className="checkout-button"
                            onClick={() => {
                                if (total > 0) {
                                    this.setState({ isCheckout: true })
                                }
                            }}
                        >
                            Checkout {`(${checkoutList.length})`}
                        </button>
                    </div>
                </div>
                
            </React.Fragment>
        )
    }
}

export default Paypal;
